mapper.update:
UPDATE USER_VOCA
   SET CHECK_READING = 1
 WHERE USER_LEARNING_SEQ = #{user_learning_seq}
   AND USER_VOCA_SEQ = #{user_voca_seq}

UPDATE USER_VOCA
   SET VOCA = #{voca},
       MEANING = #{meaning}
 WHERE USER_VOCA_SEQ = #{user_voca_seq}

UPDATE USER_VOCA
   SET LAST_VOCA = 1
 WHERE USER_VOCA_SEQ = #{user_voca_seq}

UPDATE USER_VOCA
   SET LAST_VOCA = 0
 WHERE USER_VOCA_SEQ != #{user_voca_seq}
